    //   .update({ rate: rate });
  };
  return (
    <Container>
      {[...Array(5)].map((item, index) => {
        givenRating = index + 1;
        return (
          <label>
            <Radio type="radio" value={givenRating} onClick={giveRate} />
            <Rating>
              <FaStar
                size={30}
                color={
                  givenRating < rate || givenRating === rate
