import React, { useState } from "react";
import DynamicPosts from "../DynamicPosts/DynamicPosts";

import "./Banner.css";

function Banner() {
  let [category, setCategory] = useState();

  return (
    <div className="bannerParentDiv">
      <div className="bannerChildDiv">
        <div className="menuBar">
          <div className="categoryMenu">
            {/* <select
              name="Category"
              onChange={(e) => {
                setCategory(e.target.value);
              }}
            >
              {" "}
              <option value="null">ALL CATEGORIES</option>
              <option value="Cars">Cars</option>
              <option value="Cameras & Lenses">Cameras & Lenses</option>
              <option value="Computers & Laptops">Computers & Laptops</option>
              <option value="Mobile Phones">Mobile Phones</option>
              <option value="Motorcycles">Motorcycles</option>
              <option value="Tablets">Tablets</option>
            </select> */}
          </div>
          <div className="otherQuickOptions">
            <span onClick={() => setCategory("Cars")}>Auditorium</span>
            <span onClick={() => setCategory("Cameras & Lenses")}>Gym</span>
            <span onClick={() => setCategory("Computers & Laptops")}>
              Swimming Pool
            </span>
            <span onClick={() => setCategory("Mobile Phones")}>Cafes</span>
            <span onClick={() => setCategory("Motorcycles")}>Open Grounds</span>
            <span onClick={() => setCategory("Tablets")}>Lounges</span>
          </div>
        </div>
        <div className="banner">
          <img src="../../../Images/banner copy.png" alt="" />
        </div>
      </div>
      {category != null && <DynamicPosts category={category} />}
    </div>
  );
}

export default Banner;
