import React, { useContext, useEffect, useState } from "react";
import { PostContext } from "../../contextStore/PostContext";
import { Firebase } from "../../firebase/config";
import { useHistory } from "react-router";
import "./View.css";
function View() {
  let { postContent } = useContext(PostContext); //from the global store PostContext we can get information about desired product post that we want to show (the user is clicked item on the card)

  const [userDetails, setUserDetails] = useState(); //we want show the details of who is posted the add and we dont know,so we want retreive user data from firebase who is posted this add
  const history = useHistory(); //if user click the refresh of the page then PostContext data will be erased so it will throws an error so that time we want redirect this page to home page
  useEffect(() => {
    let { userId } = postContent;
    if (userId === undefined) {
      history.push("/");
    } else {
      Firebase.firestore()
        .collection("users")
        .where("id", "==", userId)
        .get()
        .then((res) => {
          res.forEach((doc) => {
            setUserDetails(doc.data());
          });
        });
    }
  }, [history, postContent]);
  return (
    <div className="viewParentDiv">
      <div className="mainDiv">
        <div className="imageShowDiv">
          <img src={postContent.url} alt="" />
        </div>{" "}
        <div className="rightSection">
          <div className="productDetails">
            <p className="price">&#x20B9; {postContent.price} </p>
            <span>{postContent.name}</span>
            <p className="category">{postContent.category}</p>
            <span>{postContent.createdAt}</span>
            <br />
            <br />
            <span className="location">{postContent.location}</span>
          </div>
          <div className="productDescription">
            <p className="p-bold">Product Description</p>
            {/* <p>{postContent.description}</p> */}
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Laudantium, illum. Rerum labore sunt quibusdam numquam eius
              ratione deserunt aliquid nemo aliquam alias, laboriosam doloremque
              magnam doloribus corporis? Ullam, dignissimos accusantium eaque
              consectetur corporis necessitatibus animi totam dicta dolor
              suscipit ad temporibus quod unde corrupti quasi nam atque rerum.
              Similique, id accusantium et nisi animi numquam dolor quae dolore?
              In mollitia aut nihil consequatur nesciunt dolor dolorem
              inventore! Libero sapiente nisi quam natus, corrupti, hic quia
              quod a ea ad, harum facere maxime reiciendis ipsum impedit iste
              nobis omnis voluptas consectetur illum totam? Alias rem tenetur
              quasi unde natus veniam obcaecati veritatis rerum amet dolorem
              dolores provident labore, quo nesciunt sunt reprehenderit culpa
              quibusdam dolore sapiente? Pariatur eum aliquid enim. Mollitia
              praesentium, cupiditate voluptas ad temporibus debitis iure
              perferendis tenetur deserunt! Exercitationem iusto voluptatum
              eveniet, neque consectetur cum dolorem repellat aliquid impedit
              eius labore facere ipsum nemo, temporibus cumque. Explicabo earum
              nobis ipsum quod similique magni placeat ea dolorem cupiditate?
              Aliquam ducimus qui tenetur. Eius porro libero, sed, alias eum
              asperiores officia assumenda quo beatae veniam saepe dolore
              aliquid reprehenderit dicta deleniti fugit voluptas laborum
              reiciendis corporis! Deserunt neque amet quae earum quod ut
              voluptas beatae quo, provident ea voluptates hic.
            </p>
          </div>
          {userDetails && (
            <div className="contactDetails">
              <p className="p-bold">Seller details</p>
              <p>Name : {userDetails.name}</p>
              <p>Phone : {userDetails.phone}</p>
            </div>
          )}
        </div>
      </div>
      <div className="reviewDiv">Reviews</div>
    </div>
  );
}
export default View;
